$zipPath="C:\gracealoneaba\webapp_logs.zip";$extractPath="C:\gracealoneaba\webapp_logs";if(Test-Path $extractPath){Remove-Item -Recurse -Force $extractPath};New-Item -ItemType Directory -Path $extractPath|Out-Null;Expand-Archive -Path $zipPath -DestinationPath $extractPath -Force;$errLog=Get-ChildItem -Path $extractPath -Recurse -Filter "*_err.log"|Select-Object -First 1;if($null -ne $errLog){Write-Host "`n=== Displaying first 25 lines of error log ===`n" -ForegroundColor Cyan;Get-Content $errLog.FullName -TotalCount 25;Write-Host "`n(Log file path: $($errLog.FullName))" -ForegroundColor Yellow}else{Write-Host "No *_err.log file found in extracted logs." -ForegroundColor Red}
